import { View, StyleSheet, TouchableOpacity,Text,Image, Touchable } from 'react-native';
import {Marker} from 'react-native-maps';
import MapView from 'react-native-maps';
import { testDefaultGps } from './component/recoil/selectors/testSelector';
import { useRecoilState } from 'recoil';
import axios from 'axios';
import { useCallback, useRef, useState, useEffect } from 'react';
import Toast from 'react-native-easy-toast';
import { Entypo } from '@expo/vector-icons';
import { MaterialCommunityIcons } from '@expo/vector-icons';
import NewDataModal from './HomeScreen/NewDataModal';
import ClickedMarkerModal from './HomeScreen/ClickedModal';
import * as Location from 'expo-location';
import io from 'socket.io-client';
import Gps from './spot/Gps';
import SwitchSelector from "react-native-switch-selector";
import { SafeAreaProvider } from 'react-native-safe-area-context';
import { SafeAreaView } from 'react-native-safe-area-context';
import RNEventSource from 'react-native-event-source';


const ImageUrl = [
    'data:image/jpeg;base64,',
    'data:image/jpeg;base64,',
    'data:image/jpeg;base64,',
    'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS7raSez3h9QZIwPSax1M9D7JoHHfkBWCLySg&usqp=CAU',
    'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ1facy446KgNB67l8GPE9jWsLJcNqV73HNWQ&usqp=CAU'
]

const fishes = ['광어','상어','고래','참돔','돌돔','망둥어']
const socket = io('http://192.168.123.101:8082');

export default function Home() {
    const [totalMarker, setTotalMarker] = useRecoilState(testDefaultGps)
    const [HomeMarker, setHomeMarker] = useState([])
    const [id, setId] = useState(11);
    const [lat, setLat] = useState(35.0968275 + 0.01);
    const [lon, setLon] = useState(128.8538282 + 0.01);
    const [title,setTitle] = useState('배스');
    const [newData, setNewData] = useState([]);
    const [newMessage, setNewMessage] = useState([]);
    
    // 모달
    const [newDataModalVisible, setNewDataModalVisible] = useState(false);
    const [ClickedMarkerModalVisible, setClickedMarkerModalVisible] = useState(false);
    //
    
    // 마커의 키
    const [MarkerKey, setMarkerKey] = useState(0)
    const getMarkerKey = (value) => {
        setMarkerKey(value)
        setClickedMarkerModalVisible(true)
    }
    //

    //
    const mapRef = useRef(null);
    const [defaultLat, setDefaultLat] = useState(37.541);
    const [defaultLon, setdefaultLon] = useState(126.986);
    const getLocation = async() => {
        const {granted} = await Location.requestForegroundPermissionsAsync();
        console.log(granted)

        const {coords:{latitude,longitude}} = await Location.getCurrentPositionAsync();
        
        if (defaultLon !== longitude && defaultLat !== latitude) {
            setDefaultLat(longitude)
            setdefaultLon(latitude)
        }
        
        // const locate = await Location.reverseGeocodeAsync({latitude, longitude}, {useGoogleMaps:false});
        
        if (mapRef.current) {
            console.log('ananannanan')
            mapRef.current.animateToRegion({
            latitude: latitude,
            longitude: longitude,
            latitudeDelta: 0.07, // 예시로 작은 값 사용
            longitudeDelta: 0.07, // 예시로 작은 값 사용
            });
        }
    }
    //
      useEffect(()=>{
        getLocation()
        // console.log(INITIAL_REGION)
        
        
        // SSE
        // const es = new RNEventSource("http://54.206.147.12/sse/connect");

        // es.addEventListener("count", (event) => {
        // console.log('connect')
        // const eventData = JSON.parse(event.data);
        // console.log("새로운 메시지 이벤트:", eventData);
        // });

        // // SSE 연결이 닫히면 다시 연결합니다.
        // es.addEventListener("close", (event) => {
        // console.log("SSE 연결이 종료되었습니다. 재연결 중...");
        // // es.reconnect(); // 서버로 재연결합니다.
        // });

        
        // WebSocket
        socket.on('dataUpdate', (data) => {
            // 서버로부터 데이터 업데이트 이벤트가 오면 해당 데이터를 받아와서 클라이언트에 반영
            // console.log('Received updated data:', data);
            // console.log('data=',data)
            setTotalMarker(data);
            setNewData((prev)=>[...prev,data[data.length - 1]])
            const newM = [data[data.length - 1]['id'],data[data.length - 1]['title'],data[data.length - 1]['size']]
            setNewMessage((prev) => [...prev, newM].slice(-5))
            // console.log(newMessage)

        });

        return () => {
        // 컴포넌트가 언마운트될 때 SSE 연결을 닫습니다.
            // es.removeAllEventListeners();
            // es.close()
            socket.off('dataUpdate') // WebSocket 종료

        }
      },[])
    
    const handleUpdate = () => {
    // 서버로 보낼 데이터 객체를 생성합니다.
        const newData = {
            id : id, // user ID
            size : 40, // 물고기 크기
            title : fishes[Math.floor(Math.random()*fishes.length)], // 물고기 이름
            latitude : lat, 
            longitude : lon,
            src1 : ImageUrl[Math.floor(Math.random()*ImageUrl.length)]
            // 다른 원하는 속성들을 추가할 수 있습니다.
        };

        // 서버로 POST 요청을 보냅니다.
        axios.post("http://192.168.123.101:8082/update", newData)
            .then((response) => {
                // console.log("서버 응답:", response.data);
            // console.log('res= ',response.data)
            // showCopyToast({id : response.data[response.data.length - 1].id, title: response.data[response.data.length - 1].title})
            
            // setTotalMarker(response.data);
            setId(prevId => prevId + 1);
            setLat(prevLat => prevLat + Math.random()*0.12);
            setLon(prevLon => prevLon - Math.random()*0.12);
            
            })
            .catch((error) => {
            console.error("데이터 업데이트 오류:", error);
            });
        
    };
    
    const goSpot = () => {
        setState(false)
        setNewMessage([])
    }

    const goAlram = () => {
        setNewDataModalVisible(true)
        setNewMessage([])
    }
    const [state, setState] = useState(true);
    const options = [
        { label : '실시간', value:"1"},
        { label : '스팟 조회', value:"2"}
    ]
    return (
        <SafeAreaProvider style={styles.container}>
        
        {state ?
            <View>
                <SafeAreaView>
                <MapView
                    ref={mapRef}
                    style={styles.map}
                    initialRegion={{
                        latitude: lat,
                        longitude: lon,
                        latitudeDelta: 0.1,
                        longitudeDelta: 0.1,
                    }}
                    rotateEnabled={false}
                    // zoomEnabled={false}

                    >
                {totalMarker.map((item,index) => (
                    <Marker
                        key = {index}
                        coordinate={{
                        latitude: item.latitude,
                        longitude: item.longitude
                    }}
                        title={String(item.id)}
                        description={item.title}
                        icon={require("./assets/location.png")}
                        onPress={()=>getMarkerKey(index)}
                    />
                ))}
                
            </MapView>
            </SafeAreaView>
            
            <View style={styles.newModal}>
                {newData.length !== 0 ?
                <View>
                    <View style={{right : 10}}>
                        <TouchableOpacity onPress={goAlram}>
                        <Image style={{top:-18, width:80, height:80}} source={require("./assets/Fishing-unscreen.gif")}/>
                        </TouchableOpacity>
                    </View>
                    
                    <View style={styles.balloonContainer}>
                        
                        <View style={styles.balloon}>
                            <Text style={styles.alramText}>{newData.length}개의 알림이 있습니다.</Text>
                        </View>
                        <View style={styles.triangle} />
                    
                    </View>
                </View>

                    :
                    <></>
                }
            </View>

            <View style={{position:'absolute', bottom:120, marginHorizontal:30}}>
                <TouchableOpacity onPress={handleUpdate}>
                    <Entypo name="camera" size={50} color="black" />
                </TouchableOpacity>
            </View>
            
             {/* 실시간 알림 */}
            <View style={{backgroundColor: 'rgba(0, 0, 0, 0.3)',borderRadius : 10, position:'absolute', bottom : 100, left:100}}>
                {newMessage.map((item,index) => (
                    <View key={index} style={{flexDirection:'row', margin:10}}>
                        <Text style={{color:'white' , fontWeight:'bold'}}>{item[0]}님이 </Text>
                        <Text style={{color:'yellow', fontWeight:'bold'}}>{item[2]}cm {item[1]}을/를 </Text>
                        <Text style={{color:'white' , fontWeight:'bold'}}>잡았습니다.</Text>
                    </View>
                ))}
            </View>
            
            <NewDataModal newData={newData} newDataModalVisible={newDataModalVisible} setNewDataModalVisible={setNewDataModalVisible} setNewData={setNewData} ></NewDataModal>
            <ClickedMarkerModal item={totalMarker[MarkerKey]} ClickedModalVisible={ClickedMarkerModalVisible} setClickedModalVisible={setClickedMarkerModalVisible} />
            </View>
            
            :
            <Gps />
                    
            }
            <View style={{position:'absolute'}}>
                {/* <Text style={{fontSize:50}}>어디</Text> */}
                <SwitchSelector
                    initial={0}
                    style={{position:'absolute', top:70, marginLeft : 20,width:170}}
                    onPress={(value) => value === '1' ? 
                        setState(true)
                        :  goSpot()
                    }
                    options={options}
                    buttonColor={'#0b74f4'}
                    borderColor={'#0b74f4'}
                    hasPadding
                    
                >
                </SwitchSelector>
                
            </View>
            { state ? 
                <View style={{position:'absolute', right :20, bottom:110}}>
                    <TouchableOpacity 
                    // style={styles.categoryButton}
                    onPress={() => getLocation()}
                    >
                        <View style={{backgroundColor:'white', borderRadius : 30, width:40, height:40, justifyContent:'center', alignItems:'center'}}>
                        <Image source={require("./assets/gps.png")} style={{width:25, height:25}}/>
                        </View>
                    
                    </TouchableOpacity>  
                </View>
                :
                <></>
            }

        </SafeAreaProvider>
    )

}

const styles = StyleSheet.create({
    container:{
        flex:1
    },
    map:{
        width: "100%",
        height : "100%"
    },
    newModal : {
        position:'absolute', 
        top:60, right:20,
    },
    balloonContainer: {
        position: 'absolute',
        backgroundColor:'transparent',
        right : 2,
        top : 75
    },
    balloon: {
        width: 160,
        height: 40,
        backgroundColor: 'rgba(0,0,0,0.5)',
        color: 'white',
        borderRadius: 20,
        padding: 12,
        zIndex: 1,
        alignItems : 'center',
        justifyContent : 'center'
    },
    triangle: {
        position: 'absolute',
        bottom: 40,
        right: 23,
        borderTopWidth: 0,
        borderLeftWidth: 10,
        borderRightWidth: 10,
        borderBottomWidth: 10,
        borderTopColor: 'transparent',
        borderLeftColor: 'transparent',
        borderRightColor: 'transparent',
        borderBottomColor: 'rgba(0,0,0,0.5)',
        zIndex: 0,
    },
    alramText : {
        fontSize : 13, 
        fontWeight:"bold",
        position:'absolute',
        color : 'white'
    }
})